def count_handles(handles)
    return handles.length
end

def shortest_handles(handles)
    return handles.min_by{|handles|handles.length}
end

def count_handles_with_5_character(handles)
    return handles.count {|handles|handles.length == 6}
end

def count_handles_starting_with_capital(handles)
    return handles.count {|handles|handles.gsub('@', '')[1]&.match?(/[[:upper:]]/) }
end

def sort_handles_alphabetically(handles)
    return handles.sort
end

def sort_handles_by_length(handles)
    return handles.sort_by { |handles| handles.gsub('@', '').length }
end

def position_handles_epenser(handles, element)
    return handles.index(element)
end

def size_handles_to_bigger(handles, taille)
    return handles.count { |handle| handle.gsub('@', '').length == taille }
end


def welcom_screen(handles)
    loop do
        puts "1. Afficher le nombre de handles dans l'array"
        puts "2. Afficher le handle le plus court"
        puts "3. Afficher les mots à 5 caractères"
        puts "4. Afficher les handles commençant par une majuscule"
        puts "5. Afficher les handles dans l'ordre alphabétique"
        puts "6. Afficher les handles du plus petit au plus grand"
        puts "7. Afficher la position de '@epenser'"
        puts "8. Afficher la répartition des handles par taille de caractère"
        puts "9. Quitter"
        puts "Faites votre choix :"
        choice = gets.chomp.to_i

        case choice
        when 1
            puts "Il y a #{count_handles(handles)} handles dans ton array."
        when 2
            puts "Le plus court est #{shortest_handles(handles)}."
        when 3
            puts "Les mots à 5 caractères : #{count_handles_with_5_character(handles)}"
        when 4
            puts "Ceux qui commencent avec une majuscule : #{count_handles_starting_with_capital(handles)}"
        when 5
            puts "Dans l'ordre alphabétique : #{sort_handles_alphabetically(handles)}"
        when 6
            puts "Du plus petit au plus grand handle : #{sort_handles_by_length(handles)}"
        when 7
            puts "Qu'elle est la position de  @epenser ? : #{position_handles_epenser(handles, '@epenser')}"
        when 8
            puts "Classement par taille de caractère dans handles : "
            (1..15).each do |taille|
                nombre_handles = size_handles_to_bigger(handles, taille)
                puts "Nombre de handles avec #{taille} caractère(s) : #{nombre_handles}"
            end
        when 9
            break
        else
            puts "Choix invalide. Veuillez entrer un nombre entre 1 et 9."
        end
    end
end
welcom_screen(handles)